mapper.select:
select pro.id, pro.return_order_no, pro.dept_id, pro.prd_order_no,
pro.product_code, pro.product_des, pro.factory_code,
pro.factory_name, pro.prd_order_status, pro.order_status,
pro.remark, pro.tenant_id, pro.create_by, pro.create_time,
pro.update_by, pro.update_time
from wms_prd_return_order pro
left join sys_dept d on pro.dept_id = d.dept_id

     and pro.return_order_no like concat('%',#{returnOrderNo}, '%') 
     and pro.prd_order_no like concat('%',#{prdOrderNo}, '%')
     and pro.product_code = #{productCode}
     and pro.product_des = #{productDes}
     and pro.factory_code = #{factoryCode}
     and pro.factory_name like concat('%', #{factoryName}, '%')
     and pro.prd_order_status = #{prdOrderStatus}
     and pro.order_status = #{orderStatus}
     and pro.return_order_no in (select return_order_no from wms_prd_return_order_detail where material_no like concat('%', #{materialNo}, '%'))
     and pro.tenant_id = #{tenantId}

        and pro.create_time >= #{startTime}


        and pro.create_time <= DATE_ADD(#{endTime}, INTERVAL 1 DAY)


        and pro.create_time between #{startTime} and DATE_ADD(#{endTime}, INTERVAL 1 DAY)

    ${params.dataScope}

order by pro.id desc

mapper.update:
update wms_prd_return_order
return_order_no = #{returnOrderNo}, prd_order_no = #{prdOrderNo}, product_code = #{productCode}, product_des = #{productDes}, factory_code = #{factoryCode}, factory_name = #{factoryName}, prd_order_status = #{prdOrderStatus}, order_status = #{orderStatus}, remark = #{remark}, tenant_id = #{tenantId}, create_by = #{createBy}, create_time = #{createTime}, dept_id = #{deptId}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
