mapper.select:
SELECT DISTINCT spc.id AS storeProdColorId, sp.store_id, spcp.store_prod_id, spcp.store_color_id, sp.prod_art_num, spc.color_name, spcp.price, sp.over_price FROM store_product_color_price spcp LEFT JOIN store_product sp ON spcp.store_prod_id = sp.id LEFT JOIN store_product_color spc ON spcp.store_prod_id = spc.store_prod_id AND spcp.store_color_id = spc.store_color_id WHERE spcp.del_flag = 0 AND sp.store_id = #{storeId}
AND sp.prod_art_num like concat('%', #{prodArtNum}, '%')
AND sp.prod_status IN
#{item}
ORDER BY spcp.store_prod_id, spcp.store_color_id
