SELECT spc.id AS storeProdColorId, spc.color_name, spcs.size, sp.prod_art_num, spcs.other_sn_prefix AS prefixPart FROM store_product_color_size spcs JOIN store_product sp ON spcs.store_prod_id = sp.id JOIN store_product_color spc ON spcs.store_color_id = spc.store_color_id AND spcs.store_prod_id = spc.store_prod_id AND spc.del_flag = 0 WHERE spcs.del_flag = 0 AND spcs.store_id = #{storeId}
AND spcs.other_sn_prefix IN
#{item}

SELECT DISTINCT
    spcs.store_prod_id,
    spcs.store_color_id
FROM
    store_product_color_size spcs
        JOIN store_color sc ON spcs.store_color_id = sc.id AND sc.del_flag = 0 AND sc.store_id = #{storeId}
        JOIN store_product sp ON spcs.store_prod_id = sp.id AND sp.del_flag = 0 AND sp.store_id = #{storeId}
WHERE
    spcs.del_flag = 0
    AND spcs.other_sn_prefix IS NULL;
