mapper.select:
SELECT spf.store_prod_id AS storeProdId, sf.file_url AS fileUrl FROM store_product_file spf LEFT JOIN sys_file sf ON spf.file_id = sf.id WHERE spf.del_flag = 0
AND spf.store_prod_id in
#{storeProdId}
AND spf.file_type = #{fileType}
AND spf.order_num = #{orderNum}

SELECT sp.prod_art_num AS prodArtNum, sp.id AS storeProdId, sf.file_url AS fileUrl FROM store_product_file spf LEFT JOIN sys_file sf ON spf.file_id = sf.id LEFT JOIN store_product sp ON spf.store_prod_id = sp.id WHERE spf.del_flag = 0 AND spf.file_type = #{fileType} AND spf.store_id = #{storeId}
AND spf.store_prod_id in
#{storeProdId}
