mapper.select:
SELECT spf.store_prod_id AS storeProdId, sf.file_url AS fileUrl FROM store_product_file spf JOIN sys_file sf ON spf.file_id = sf.id WHERE spf.del_flag = 0
AND spf.store_prod_id in
#{storeProdId}
AND spf.file_type = #{fileType}
AND spf.order_num = #{orderNum}

WITH RankedProducts AS ( SELECT spf.store_prod_id, spf.store_id, sp.prod_art_num, sf.file_url AS mainPicUrl, ROW_NUMBER() OVER ( PARTITION BY spf.store_id ORDER BY spf.create_time DESC ) AS rank_num FROM store_product_file spf JOIN store_product sp ON spf.store_prod_id = sp.id JOIN sys_file sf ON spf.file_id = sf.id JOIN store s ON spf.store_id = s.id WHERE spf.del_flag = 0 AND spf.file_type = 1 AND spf.order_num = 1
AND spf.store_id IN
#{storeId}
) SELECT store_prod_id, store_id, prod_art_num, mainPicUrl FROM RankedProducts WHERE rank_num <= 4 ORDER BY store_id, rank_num

SELECT spf.store_prod_id, spf.file_id, sf.file_url, sf.file_name, sf.file_size, spf.file_type, spf.order_num FROM store_product_file spf JOIN sys_file sf ON spf.file_id = sf.id WHERE spf.del_flag = 0 AND spf.store_prod_id = #{storeProdId}
AND spf.file_type IN
#{fileType}

SELECT spf.store_id, sf.file_url AS mainPicUrl FROM store_product_file spf JOIN sys_file sf ON spf.file_id = sf.id WHERE spf.del_flag = 0 AND spf.store_id IN
#{id}
AND spf.file_type = 1 AND spf.id = ( SELECT id FROM store_product_file spf2 WHERE spf2.store_id = spf.store_id AND spf2.del_flag = 0 AND spf2.file_type = 1 AND spf2.order_num = 1 ORDER BY spf2.create_time DESC LIMIT 1 );
