SELECT uf.id AS user_favorite_id, s.id AS store_id, uf.store_prod_id, s.store_name, sp.prod_title, uf.create_time, sp.prod_art_num, sp.prod_status FROM user_favorites uf JOIN store_product sp ON uf.store_prod_id = sp.id JOIN store s ON uf.store_id = s.id WHERE uf.del_flag = 0 AND uf.user_id = #{userId}
AND sp.prod_status IN
#{item}
AND s.store_name like concat('%', #{storeName}, '%') AND sp.prod_art_num like concat('%', #{prodArtNum}, '%')
